import React, { useState } from "react";
import { Carousel, Container, Row, Col, Modal } from "react-bootstrap";
import Items from "../../items.json";
import "./index.css";


const Shop = () => {
  // const [show, setShow] = useState(false);
  // const handleClose = () => setShow(false);
  // const handleShow = () => setShow(true);
  return <div>
    <div className="container-fluid shop-header">Welcome to Samaria Jewelry</div>
    <div className="container-fluid shop-sub-header mb-5">From the Holy Land</div>
    {/*  */}
    <Carousel variant="dark" className="mb-5">
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="First slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Second slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Third slide"
        />
      </Carousel.Item>
    </Carousel>

    {/*  */}
    <div className="items mb-5">
      <Container fluid>

        <Row className="row-cont">
          {Items.map(item => {
            return (
              <Col key={item.id} className="gy-5 item-cont" sm={12} md={4} lg={3}>
                <img className="item-image w-100" src={"images/items/item" + item.id + "/main.jpg"} alt={item.name} onMouseOver={() => handleShow()} onMouseOut={() => handleClose()}/>
              </Col>
            )
          })}
        </Row>
      </Container>
    </div>
{/* <Modal show={true}>
  <Modal.Body>
    Test
  </Modal.Body>
</Modal> */}
  </div>
}

export default Shop;
