import React, { useRef } from "react";
import { Carousel, Container, Row, Col} from "react-bootstrap";
import Items from "../../items.json";
import "./index.css";


const Shop = () => {
  // const [show, setShow] = useState(false);
  // const handleClose = () => setShow(false);
  // const handleShow = () => setShow(true);
  const colRef = useRef<HTMLDivElement>(null);
  const handleHover = () => {
    const width = colRef.current?.getBoundingClientRect().width;
    const top = colRef.current?.getBoundingClientRect().top;
    const left = colRef.current?.getBoundingClientRect().left;
  };
  return <div>
    <div className="container-fluid shop-header">Welcome to Samaria Jewelry</div>
    <div className="container-fluid shop-sub-header mb-5">From the Holy Land</div>
    {/*  */}
    <Carousel variant="dark" className="mb-5">
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="First slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Second slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Third slide"
        />
      </Carousel.Item>
    </Carousel>

    {/*  */}
    <div className="items mb-5">
      <Container fluid>

        <Row className="row-cont">
          {Items.map(item => {
            return (
              <Col ref={colRef} key={item.id} className="gy-5 item-cont" sm={12} md={4} lg={3} onMouseOver={() => handleHover()}>
                <img className="item-image w-100" src={"images/items/item" + item.id + "/main.jpg"} alt={item.name}/>
              </Col>
            )
          })}
        </Row>
      </Container>
    </div>
{/* <Modal show={true}>
  <Modal.Body>
    Test
  </Modal.Body>
</Modal> */}
  </div>
}

export default Shop;
