import React, { useState } from "react";
import { Carousel, Container, Row, Col, Modal } from "react-bootstrap";
import Items from "../../items.json";
import "./index.css";


const Shop = () => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const [itemName, setItemName] = useState("");
  const handleClick = (name:string) => {
    setShow(true);
    setItemName(name)
  }
  return <div>
    <div className="container-fluid shop-header">Welcome to Samaria Jewelry</div>
    <div className="container-fluid shop-sub-header mb-5">From the Holy Land</div>
    {/*  */}
    <Carousel variant="dark" className="mb-5">
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="First slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Second slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Third slide"
        />
      </Carousel.Item>
    </Carousel>

    {/*  */}
    <div className="items mb-5">
      <Container fluid>

        <Row>
          {Items.map(item => {
            return (
              <Col className="gy-5" sm={12} md={4} lg={3} onClick={() => handleClick(item.name)}>
                <img className="item-image" src={"images/items/item" + item.id + "/main.jpg"} alt={item.name} />
              </Col>
            )
          })}
        </Row>
      </Container>
    </div>
<Modal show={show} onHide={handleClose}>
  <Modal.Header closeButton>
    <Modal.Title>{itemName}</Modal.Title>
    </Modal.Header>
  <Modal.Body>
          test
  </Modal.Body>
</Modal>
  </div>
}

export default Shop;
