import React, { useEffect, useState } from "react";
import { Carousel, Container, Row, Col, Modal } from "react-bootstrap";
import { Item } from "../../types";
import Items from "../../items.json";
import "./index.css";



const Shop = () => {
  const [storeItems, setStoreItems] = useState<Item[]>();
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const [itemId, setItemId] = useState<number>();
  const [itemName, setItemName] = useState("");
  const [itemDescription, setItemDescription] = useState("");
  const [itemPrice, setItemPrice] = useState<number>();
  const [numOfOtherImages, setNumOfOtherImages] = useState<number>();
  const showItemDetails = (item: Item) => {
    setShow(true);
    setItemId(item.id);
    setItemName(item.name);
    setItemDescription(item.description);
    setItemPrice(item.price);
    setNumOfOtherImages(item.numOfOtherImage);
  }

  useEffect(() => { setStoreItems(Items) }, []);

  return <div>
    <div className="container-fluid shop-header">Welcome to Samaria Jewelry</div>
    <div className="container-fluid shop-sub-header mb-5">From the Holy Land</div>
    {/*  */}
    <Carousel variant="dark" className="mb-5">
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="First slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Second slide"
        />
      </Carousel.Item>
      <Carousel.Item interval={2000}>
        <img
          className="d-block w-100 carousel-image"
          src="images/shop-images.jpg"
          alt="Third slide"
        />
      </Carousel.Item>
    </Carousel>

    {/*  */}
    <div className="items mb-5">
      <Container fluid>

        <Row>
          {storeItems?.map((item: Item) => {
            return (
              <Col className="gy-5" sm={12} md={4} lg={3} onClick={() => showItemDetails(item)}>
                <img className="item-image" src={"images/items/item" + item.id + "/main.jpg"} alt={item.name} />
              </Col>
            )
          })}
        </Row>
      </Container>
    </div>
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{itemName}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Carousel variant="dark">
          {
          
          Array.from(Array(numOfOtherImages), (_, index) => index +1).map((_, index) => {

              return (
                <Carousel.Item>
                  <img
                    className="d-block w-100"
                    src={"images/items/item" + itemId + "/" + index + ".jpg"}
                    alt="First slide"
                  />
                </Carousel.Item>
              )
            })
          }



        </Carousel>
        {itemDescription}<br />
        ${itemPrice}
      </Modal.Body>
    </Modal>
  </div>
}

export default Shop;
